import React, { useState } from 'react';

const AddPlayerForm = () => {

    const [value, setValue] = useState('');
    return (
        <form>
        {console.log(value)}
            <input
                 type = 'text'
                 value = {value}
                 placeholder='Enter a player name'
                 onChange = { (event) => setValue(event.target.value) }
            />
               
            <input
                type = 'submit'
                value = 'Add Player'
            />
        </form>

    );

}




export default AddPlayerForm;
